//This component is responsible for rendering the hexagonal tiles and the interactivity.

import React, { useState } from 'react';
import { Hexagon, Text } from 'react-hexgrid';
import configs from './configurations';
import './hex.css';

const InteractiveHexagon = ({ tile }) => {
    const [isActive, setIsActive] = useState(false);

    const handleClick = () => {
        setIsActive(!isActive);
        console.log(`Hexagon clicked: q: ${tile.q}, r: ${tile.r}, s: ${tile.s}`);
    };

    const fillColor = determineFillColor(tile.biome);

    return (
        <Hexagon
            q={tile.q}
            r={tile.r}
            s={tile.s}
            size={configs.hexSize}
            fill={isActive ? 'red' : fillColor}
            onClick={handleClick}
            className={`hexagon-group ${isActive ? 'active' : ''}`}
        >
            <Text className="hexagon-text">{`${tile.q},${tile.r},${tile.s}`}</Text>
        </Hexagon>
    );

    function determineFillColor(biome) {
        switch (biome) {
            case "Rock":
                return "url(#pat1)";
            case "Mountain":
                return "url(#pat2)";
            default:
                return "none";
        }
    }
};

export default InteractiveHexagon;
