//This component is responsible for rendering the hexagonal tiles and the interactivity.

import React, { useState } from 'react';
import { Hexagon, Text } from 'react-hexgrid';
import configs from './configurations'
import './hex.css';
import tile from './Tile';

const InteractiveHexagon = ({ tile }) => {
    const [isActive, setIsActive] = useState(false);
  // Define what happens when you click on a hexagon
    const handleClick = () => {
    console.log('Hexagon clicked');
    console.log(tile.getTileInfo())
    console.log("----------------------------------------------------")
    console.log(`"q": ${tile.q}, "r": ${tile.r}, "s": ${tile.s}`);
    setIsActive(!isActive); // Toggle the active state
    console.log(isActive); 
    
  };

  // Customize the appearance of the hexagon based on the Tile properties
  // For example, changing color based on biome
  const fillColor = tile.biome ? determineColorBasedOnBiome(tile.biome) : "gray"; 
  const stroke = isActive ? 'red' : 'none'; // Use 'none' or your default color


  return (
    <Hexagon 
    q={tile.q} 
    r={tile.r} 
    s={tile.s} 
    size={configs.hexSize} 
    fill={fillColor} 
    stroke={stroke} 
    onClick={handleClick}
    className="hexagon-group"
    >
      <Text className="hexagon-text">{`${tile.q},${tile.r},${tile.s}`}</Text>
      {/* Additional SVG elements as needed, e.g., resource icons */}
    </Hexagon>
  );
};

// A helper function to determine hexagon color based on its biome
function determineColorBasedOnBiome(biome) {
  switch(biome) {
    case "Forest": return "green";
    case "Desert": return "yellow";
    // Add more cases as needed
    default: return "gray";
  }
}

export default InteractiveHexagon;
