//This component is responsible for rendering the hexagonal tiles and the interactivity.

import React, { Component } from 'react';
import { Hexagon, Text } from 'react-hexgrid';
import GameSettings from './GameSettings';

const InteractiveHexagon = ({ q, r, s }) => {
  // Define what happens when you click on a hexagon
  const handleClick = () => {
    console.log(`Hexagon clicked: ${q},${r},${s}`);
    // Add more click handling logic as needed
  };

  return (
    <Hexagon q={q} r={r} s={s} size={GameSettings.hexSize} onClick={handleClick}>
      <Text>{`${q},${r},${s}`}</Text>
      {/* Add more SVG elements as needed */}
    </Hexagon>
  );
};

export default InteractiveHexagon;
