// Board.js
import React, { Component } from 'react';
import { HexGrid, Layout, GridGenerator } from 'react-hexgrid';
import InteractiveHexagon from './InteractiveHexagon';
import Tile from './Tile';
import configs from './configurations';
import gapData from './gapCoordinates.json';
import plains from './plains.png'; // Update the path as necessary
import mountain from './mountain.png'; // Update the path as necessary

// Define the Patterns component
const Patterns = () => (
    <defs>
        <pattern id="pat1" patternUnits="userSpaceOnUse" width="30" height="30">
            <image href={plains} x="0" y="0" width="30" height="30" />
        </pattern>
        <pattern id="pat2" patternUnits="userSpaceOnUse" width="30" height="30">
            <image href={mountain} x="0" y="0" width="30" height="30" />
        </pattern>
    </defs>
);

class Board extends Component {
    constructor(props) {
        super(props);
        const config = configs['rectangle'];
        const generator = GridGenerator.getGenerator(config.map);
        const hexCoordinates = generator.apply(this, config.mapProps);
        const gapCoordinates = gapData.gapCoordinates;
        const gapSet = new Set(gapCoordinates.map(coord => `${coord.q},${coord.r},${coord.s}`));
        const hexagons = hexCoordinates.filter(hexCoord => !gapSet.has(`${hexCoord.q},${hexCoord.r},${hexCoord.s}`))
                                       .map(hexCoord => new Tile(hexCoord.q, hexCoord.r, hexCoord.s));
        this.state = { hexagons, config, viewBox: `${config.viewBox.x} ${config.viewBox.y} ${config.viewBox.width} ${config.viewBox.height}` };
    }

    render() {
        const { hexagons, config, viewBox } = this.state;
        const size = { x: config.layout.width, y: config.layout.height };
        const containerStyle = { width: '100%', overflow: 'auto', maxHeight: '100vh' };

        return (
            <React.Fragment>
                {plainsPattern}
                {mountainPattern}
            <div style={containerStyle}>
                <HexGrid width={config.width} height={config.height} viewBox={viewBox}>
                    <Patterns />
                    <Layout size={size} flat={config.layout.flat} spacing={config.layout.spacing} origin={config.origin}>
                        {hexagons.map((hex, i) => (
                            <InteractiveHexagon key={`${hex.q},${hex.r},${hex.s}`} q={hex.q} r={hex.r} s={hex.s} size={size} tile={hex} />
                        ))}
                    </Layout>
                </HexGrid>
            </div>
            </React.Fragment>
        );

        
    }
}

const RotatedPattern = ({ id, link }) => (
    <svg style={{ height: 0 }}>
      <defs>
        <pattern id={id} patternUnits="objectBoundingBox" width="1" height="1" viewBox="0 0 30 30" preserveAspectRatio="xMidYMid slice">
          <g transform="rotate(30, 15, 15)"> {/* Rotate around the center */}
            <image href={link} x="0" y="0" width="30" height="30" preserveAspectRatio="xMidYMid slice" />
          </g>
        </pattern>
      </defs>
    </svg>
  );

export default Board;
