// InteractiveHexagon.js
import React, { useState } from 'react';
import { Hexagon, Text } from 'react-hexgrid';
import configs from './configurations';
import './hex.css';
import plains from './plains.png'; // Update the path as necessary
import mountain from './mountain.png'; // Update the path as necessary

const InteractiveHexagon = ({ tile }) => {
    const [isActive, setIsActive] = useState(false);

    const handleClick = () => {
        console.log('Hexagon clicked');
        console.log(tile.getTileInfo());
        setIsActive(!isActive);
    };
    const patternId1 = 'patternPlains';
    const patternId2 = 'patternMountain';
    const plainsPattern = this.tile.biome === "Plains" ? (
      <RotatedPattern id={patternId1} link={plains} size={{ x: 30, y: 30 }} />
    ) : null;

    const mountainPattern = this.tile.biome === "Mountain" ? (
      <RotatedPattern id={patternId2} link={mountain} size={{ x: 30, y: 30 }} />
    ) : null;

    const fillColor = tile.biome === "Plains" ? "url(#pat1)" : 
                     tile.biome === "Mountain" ? "url(#pat2)" : 
                     "none";
    return (
        <React.Fragment>
        {plainsPattern}
        {mountainPattern}
        <Hexagon q={tile.q} r={tile.r} s={tile.s} size={configs.hexSize} fill={isActive ? 'red' : fillColor} onClick={handleClick} className={`hexagon-group ${isActive ? 'active' : ''}`}>
            <Text className="hexagon-text">{`${tile.q},${tile.r},${tile.s}`}</Text>
        </Hexagon>
        </React.Fragment>
    );
};

const RotatedPattern = ({ id, link }) => (
    <svg style={{ height: 0 }}>
      <defs>
        <pattern id={id} patternUnits="objectBoundingBox" width="1" height="1" viewBox="0 0 30 30" preserveAspectRatio="xMidYMid slice">
          <g transform="rotate(30, 15, 15)"> {/* Rotate around the center */}
            <image href={link} x="0" y="0" width="30" height="30" preserveAspectRatio="xMidYMid slice" />
          </g>
        </pattern>
      </defs>
    </svg>
  );

export default InteractiveHexagon;
